# '''


from collections import deque


graph_ = {1:[2],2:[1,3,7],3:[2,5] ,4:[6] ,5:[3,7],6:[4],7:[2,5] }
storebfs=[]
n=len(graph_)
vis=[0]*(n+1)
q  = deque()
for i in range(1,n+1):
    if not vis[i]:
        q.append(i)
    vis[i] = True
    while len(q):
        node = q.popleft()
        storebfs.append(node)
        for elem in graph_[node]:
            if not vis[elem]:
                q.append(elem)
                vis[elem]=True

print(storebfs)


